var app=angular.module("LocalList",[]);app.controller("TasksController",["$scope","$filter",function(e,t){e.tasks=[],e.get_tasks=function(e,s){return t("filter")(e,{completed:s})},e.add_new_task=function(t){e.tasks.push({description:t,completed:!1}),e.new_task=null},e.keypressed_on_new_task=function(t,s){switch(t.keyCode){case 13:e.add_new_task(s)}},e.remove_task=function(t){e.tasks.splice(e.tasks.indexOf(t),1)},e.complete_task=function(e){e.completed=!0,e.completedDate=(new Date).getTime()}}]);